export default function Home() {
  return (
    <>

    <Section />

    </>
  )
}


export function Section() {

    const [theme, setTheme] = useState(false);
    const colors = {
        bacground: '#212120',
        color: "white"
    }
    const handleTheme = () => {
        setTheme(currentValue => (!currentValue))
    } 

  return (
    <div className='section'>
        <button
        style={colors}
        onClick={handleTheme}>{theme  ? "Dark theme" : "Light theme"}</button>
