import './App.css';
import { Camera } from 'react-camera-pro';
import { useRef, useState } from 'react';
function App() {

const camera = useRef(null)
const [img, setImg] = useState(null)

  return (
    <div className="App">
      <Camera ref={camera}/>
      <button onClick={() => setImg(camera.current.takePhoto())}>Take Photo</button>
      <img src={img} alt='take photo'/>
    </div>
  );
}

export default App;
